import XCTest
import CloverConnector

class Test_remotepaylib_TerminalMessage: XCTestCase {
    
    override func setUp() {
        super.setUp()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: - Serialization
    //
    // This test takes a TerminalMessage object, serializes it to JSON, and compares the results using introspection to a
    // set of tests designed to check that the output JSON is properly formatted.
    //
    func testSerialization() {
        let testMessage = TerminalMessage()
        testMessage.text = "Test Message"
        XCTAssert(runContentsTestSerialization(input: testMessage, output: ["version":(test:1,pass:true),"method":(test:"TERMINAL_MESSAGE",pass:true),"text":(test:"Test Message",pass:true)]))
        XCTAssert(runContentsTestSerialization(input: testMessage, output: ["version":(test:2,pass:false),"method":(test:"TERMINAL_MESSAGE",pass:true),"text":(test:"Test Message",pass:true)]))
        
        testMessage.text = "01234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789"
        XCTAssert(runContentsTestSerialization(input: testMessage, output: ["version":(test:1,pass:true),"method":(test:"TERMINAL_MESSAGE",pass:true),"text":(test:"01234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789",pass:true)]))
        
        testMessage.text = nil
        XCTAssert(runContentsTestSerialization(input: testMessage, output: ["version":(test:1,pass:true),"method":(test:"TERMINAL_MESSAGE",pass:true),"text":(test:nil,pass:true)]))
        
        testMessage.text = ""
        XCTAssert(runContentsTestSerialization(input: testMessage, output: ["version":(test:1,pass:true),"method":(test:"TERMINAL_MESSAGE",pass:true),"text":(test:"",pass:true)]))

        testMessage.text = "üçî"
        XCTAssert(runContentsTestSerialization(input: testMessage, output: ["version":(test:1,pass:true),"method":(test:"TERMINAL_MESSAGE",pass:true),"text":(test:"üçî",pass:true)]))
        
        testMessage.text = ""
        XCTAssert(runContentsTestSerialization(input: testMessage, output: ["version":(test:1,pass:true),"method":(test:"TERMINAL_MESSAGE",pass:true),"text":(test:"",pass:true)]))
        
        

    }
    
    // MARK: - Serialization
    //
    // This test takes a JSON representation of a TerminalMessage object, deserializes it into a TerminalMessage,
    // and compares it to an actual TerminalMessage object.
    //
    func testDeserialization() {
        let message = TerminalMessage()
        message.text = "Test Message"
        message.version = 1
        XCTAssert(runContentsTestDeserializationPass(input: "{\"version\":1,\"method\":\"TERMINAL_MESSAGE\",\"text\":\"Test Message\"}", output: message))
        XCTAssert(!runContentsTestDeserializationPass(input: "{\"version\":2,\"method\":\"TERMINAL_MESSAGE\",\"text\":\"Test Message\"}", output: message))
        message.text = nil
        XCTAssert(runContentsTestDeserializationPass(input: "{\"version\":1,\"method\":\"TERMINAL_MESSAGE\"}", output: message))
        message.version = -1
        XCTAssert(runContentsTestDeserializationPass(input: "{\"version\":-1,\"method\":\"TERMINAL_MESSAGE\"}", output: message))
        message.version = 99999999999999
        XCTAssert(runContentsTestDeserializationPass(input: "{\"version\":99999999999999,\"method\":\"TERMINAL_MESSAGE\"}", output: message))
        
        // version wrong type
        XCTAssert(!runContentsTestDeserializationPass(input: "{\"version\":\"a\",\"method\":\"TERMINAL_MESSAGE\"}", output: message))
        // invalid JSON
        XCTAssert(!runContentsTestDeserializationPass(input: "{\"version\":a,\"method\":\"TERMINAL_MESSAGE\"}", output: message))
        
        XCTAssert(runContentsTestDeserializationPass(input: "{\"version\":99999999999999,\"method\":\"01234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789\"}", output: message))
        
        print("\(#file.components(separatedBy: "/").last ?? "") \(#function):\(#line)")
    }
    
    
    
}
